import React, { useState, useRef } from 'react';
import './App.css';
import { GoogleGenerativeAI } from '@google/generative-ai';

// Initialize Gemini AI
const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);
  const [question, setQuestion] = useState('');
  const [edithResponse, setEdithResponse] = useState('');
  const [isAsking, setIsAsking] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // File handling
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      setAnalysisResult('');
    }
  };

  // Convert file to base64
  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = error => reject(error);
    });
  };

  // Simple drawing on canvas
  const enableDrawing = () => {
    setIsDrawing(true);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let drawing = false;

    const startDrawing = (e) => {
      drawing = true;
      draw(e);
    };

    const draw = (e) => {
      if (!drawing) return;

      const rect = canvas.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      ctx.lineWidth = 3;
      ctx.lineCap = 'round';
      ctx.strokeStyle = '#ff0000';

      ctx.lineTo(x, y);
      ctx.stroke();
      ctx.beginPath();
      ctx.moveTo(x, y);
    };

    const stopDrawing = () => {
      drawing = false;
      ctx.beginPath();
    };

    canvas.addEventListener('mousedown', startDrawing);
    canvas.addEventListener('mousemove', draw);
    canvas.addEventListener('mouseup', stopDrawing);
    canvas.addEventListener('mouseout', stopDrawing);
  };

  // Clear canvas
  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  // Ask EDITH a question
  const askEdith = async () => {
    if (!question.trim() || !analysisResult) {
      alert('Please analyze a video first and enter a question!');
      return;
    }

    setIsAsking(true);
    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      const prompt = `Based on this video analysis: "${analysisResult}"

      Please answer this question as EDITH (Educational Digital Intelligence Teaching Helper): "${question}"

      Provide a helpful, educational response.`;

      const result = await model.generateContent(prompt);
      setEdithResponse(result.response.text());
    } catch (error) {
      console.error('Error asking EDITH:', error);
      setEdithResponse('Sorry, I had trouble processing that question.');
    } finally {
      setIsAsking(false);
    }
  };

  // Analyze video
  const analyzeVideo = async () => {
    if (!selectedFile) return;

    setIsAnalyzing(true);

    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const base64Data = await fileToBase64(selectedFile);
      const videoData = base64Data.split(',')[1];

      const result = await model.generateContent([
        "Analyze this video and provide a comprehensive summary of the content, key points, and any educational material shown.",
        {
          inlineData: {
            mimeType: selectedFile.type,
            data: videoData
          }
        }
      ]);

      setAnalysisResult(result.response.text());

    } catch (error) {
      console.error('Error:', error);
      setAnalysisResult('Error analyzing video.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>📹 Video Analyzer & Drawing Tool</h1>
        <p>Upload a video, analyze it, and draw notes</p>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <input type="file" accept="video/*" onChange={handleFileSelect} />
          {selectedFile && <p>Selected: {selectedFile.name}</p>}

          <button onClick={analyzeVideo} disabled={!selectedFile || isAnalyzing}>
            {isAnalyzing ? 'Analyzing...' : 'Analyze Video'}
          </button>
        </div>

        <video ref={videoRef} style={{ display: 'none' }} />

        {/* Analysis Results */}
        {analysisResult && (
          <div className="analysis-section" style={{ marginBottom: '20px' }}>
            <h3>📋 Video Analysis Summary:</h3>
            <div style={{ 
              padding: '15px',
              backgroundColor: '#f8f9fa',
              border: '1px solid #dee2e6',
              borderRadius: '5px',
              whiteSpace: 'pre-wrap',
              textAlign: 'left',
              maxHeight: '300px',
              overflowY: 'auto'
            }}>
              {analysisResult}
            </div>
          </div>
        )}

        {/* Ask EDITH Section */}
        {analysisResult && (
          <div className="edith-section" style={{ marginBottom: '20px' }}>
            <h3>🤖 Ask EDITH a Question:</h3>
            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
              <input
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Ask EDITH about the video content..."
                style={{
                  flex: 1,
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                  fontSize: '16px'
                }}
                onKeyPress={(e) => e.key === 'Enter' && askEdith()}
              />
              <button
                onClick={askEdith}
                disabled={isAsking || !question.trim()}
                style={{
                  padding: '10px 20px',
                  backgroundColor: isAsking ? '#6c757d' : '#28a745',
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                {isAsking ? 'Asking...' : 'Ask EDITH'}
              </button>
            </div>

            {edithResponse && (
              <div style={{
                marginTop: '15px',
                padding: '15px',
                backgroundColor: '#e8f5e8',
                border: '1px solid #28a745',
                borderRadius: '5px',
                whiteSpace: 'pre-wrap'
              }}>
                <strong>EDITH:</strong> {edithResponse}
              </div>
            )}
          </div>
        )}

        {/* Drawing Canvas */}
        <div className="canvas-section">
          <div style={{ marginBottom: '10px' }}>
            <button 
              onClick={enableDrawing} 
              disabled={isDrawing}
              style={{ 
                padding: '10px 20px', 
                marginRight: '10px',
                backgroundColor: isDrawing ? '#28a745' : '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              {isDrawing ? '�️ Drawing Enabled' : '🖊️ Enable Drawing'}
            </button>
            <button 
              onClick={clearCanvas}
              style={{ 
                padding: '10px 20px',
                backgroundColor: '#dc3545',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              🗑️ Clear Canvas
            </button>
          </div>

          <canvas 
            ref={canvasRef}
            width="800" 
            height="600"
            style={{ 
              border: '2px solid #333',
              backgroundColor: '#f8f9fa',
              cursor: isDrawing ? 'crosshair' : 'default'
            }}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
