import React, { useState, useRef } from "react";
import "./App.css";

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState("");
  const [question, setQuestion] = useState("");
  const [isAsking, setIsAsking] = useState(false);
  const [edithResponse, setEdithResponse] = useState("");
  const [isDrawing, setIsDrawing] = useState(false);

  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const fileInputRef = useRef(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith("video/")) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      if (videoRef.current) {
        videoRef.current.src = url;
      }
    } else {
      alert("Please select a video file");
    }
  };

  const analyzeVideo = async () => {
    if (!selectedFile) {
      alert("Please select a video file first");
      return;
    }
    setIsAnalyzing(true);
    setTimeout(() => {
      setAnalysisResult("Video analysis complete! This is a sample analysis result. The video contains educational content about mathematics and includes whiteboard demonstrations.");
      setIsAnalyzing(false);
    }, 2000);
  };

  const askEdith = async () => {
    if (!question.trim()) {
      alert("Please enter a question");
      return;
    }
    setIsAsking(true);
    setTimeout(() => {
      setEdithResponse(`Sample response to: "${question}". This is how EDITH would respond to your question about the video content.`);
      setIsAsking(false);
    }, 1500);
  };

  const enableDrawing = () => {
    setIsDrawing(!isDrawing);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext("2d");
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  };

  return (
    <div className="App" style={{ textAlign: "center", background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)", minHeight: "100vh", color: "white" }}>
      <header style={{ padding: "20px", background: "rgba(0, 0, 0, 0.1)" }}>
        <h1>üéì Lecture Analysis & Interactive Whiteboard</h1>
        <p>Upload educational videos for AI analysis and interactive drawing</p>
      </header>

      <main style={{ padding: "20px", maxWidth: "1200px", margin: "0 auto" }}>
        <div style={{ marginBottom: "30px" }}>
          <input type="file" accept="video/*" onChange={handleFileSelect} ref={fileInputRef} style={{ display: "none" }} />
          <button onClick={() => fileInputRef.current?.click()} style={{ background: "linear-gradient(45deg, #ff6b6b, #ee5a24)", color: "white", border: "none", padding: "20px 40px", fontSize: "24px", borderRadius: "15px", cursor: "pointer", boxShadow: "0 8px 15px rgba(0, 0, 0, 0.2)" }}>üìÅ Choose Video File</button>
          {selectedFile && <p style={{ marginTop: "15px", fontSize: "18px", background: "rgba(255, 255, 255, 0.1)", padding: "10px", borderRadius: "8px", display: "inline-block" }}>Selected: {selectedFile.name}</p>}
        </div>

        {selectedFile && (
          <div style={{ margin: "30px 0", background: "rgba(255, 255, 255, 0.1)", padding: "20px", borderRadius: "15px" }}>
            <video ref={videoRef} controls style={{ maxWidth: "100%", height: "auto", borderRadius: "10px", marginBottom: "20px" }} />
            <div style={{ display: "flex", gap: "15px", justifyContent: "center", flexWrap: "wrap" }}>
              <button onClick={analyzeVideo} disabled={isAnalyzing} style={{ padding: "15px 30px", fontSize: "16px", border: "none", borderRadius: "10px", cursor: "pointer", background: "linear-gradient(45deg, #4ecdc4, #44a08d)", color: "white" }}>{isAnalyzing ? "üîÑ Analyzing..." : "ü§ñ Analyze Video"}</button>
              <button onClick={enableDrawing} style={{ padding: "15px 30px", fontSize: "16px", border: "none", borderRadius: "10px", cursor: "pointer", background: isDrawing ? "linear-gradient(45deg, #ff6b6b, #ee5a24)" : "linear-gradient(45deg, #ffecd2, #fcb69f)", color: isDrawing ? "white" : "#333" }}>{isDrawing ? "‚úèÔ∏è Drawing On" : "‚úèÔ∏è Enable Drawing"}</button>
              <button onClick={clearCanvas} style={{ padding: "15px 30px", fontSize: "16px", border: "none", borderRadius: "10px", cursor: "pointer", background: "linear-gradient(45deg, #ff9a8b, #f6416c)", color: "white" }}>üóëÔ∏è Clear Drawing</button>
            </div>
          </div>
        )}

        {analysisResult && (
          <div style={{ background: "rgba(255, 255, 255, 0.15)", margin: "40px 0", padding: "30px", borderRadius: "20px", backdropFilter: "blur(10px)", border: "1px solid rgba(255, 255, 255, 0.2)" }}>
            <h2 style={{ fontSize: "32px", marginBottom: "25px", color: "#fff" }}>üìä Video Analysis Summary</h2>
            <div style={{ background: "rgba(0, 0, 0, 0.3)", padding: "25px", borderRadius: "15px", border: "1px solid rgba(255, 255, 255, 0.1)" }}>
              <pre style={{ fontSize: "16px", lineHeight: "1.8", color: "#fff", textAlign: "left", whiteSpace: "pre-wrap" }}>{analysisResult}</pre>
            </div>
          </div>
        )}

        <div style={{ background: "rgba(255, 255, 255, 0.1)", padding: "30px", borderRadius: "20px", margin: "30px 0" }}>
          <h2 style={{ fontSize: "28px", marginBottom: "25px" }}>ü§î Ask EDITH Questions</h2>
          <div style={{ display: "flex", gap: "15px", marginBottom: "25px", alignItems: "center", justifyContent: "center", flexWrap: "wrap" }}>
            <input type="text" value={question} onChange={(e) => setQuestion(e.target.value)} placeholder="Ask a question about the video content..." style={{ flex: 1, minWidth: "300px", padding: "15px 20px", fontSize: "16px", border: "2px solid rgba(255, 255, 255, 0.2)", borderRadius: "10px", background: "rgba(255, 255, 255, 0.1)", color: "white" }} onKeyPress={(e) => e.key === "Enter" && askEdith()} />
            <button onClick={askEdith} disabled={isAsking} style={{ padding: "15px 30px", fontSize: "16px", border: "none", borderRadius: "10px", cursor: "pointer", background: "linear-gradient(45deg, #a8edea, #fed6e3)", color: "#333" }}>{isAsking ? "üîÑ Asking..." : "ü§ñ Ask EDITH"}</button>
          </div>
          {edithResponse && (
            <div style={{ background: "rgba(0, 0, 0, 0.2)", padding: "25px", borderRadius: "15px", borderLeft: "5px solid #4ecdc4" }}>
              <h3 style={{ color: "#4ecdc4", marginBottom: "15px", fontSize: "22px" }}>üí¨ EDITH Response:</h3>
              <p style={{ fontSize: "16px", lineHeight: "1.7", textAlign: "left" }}>{edithResponse}</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
