import React, { useState, useRef } from 'react';
import './App.css';
import { GoogleGenerativeAI } from '@google/generative-ai';

// Initialize Gemini AI
const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);
  const [question, setQuestion] = useState('');
  const [edithResponse, setEdithResponse] = useState('');
  const [isAsking, setIsAsking] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // File handling
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      setAnalysisResult('');
    }
  };

  // Convert file to base64
  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = error => reject(error);
    });
  };

  // Simple drawing on canvas
  const enableDrawing = () => {
    setIsDrawing(true);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let drawing = false;

    const startDrawing = (e) => {
      drawing = true;
      draw(e);
    };

    const draw = (e) => {
      if (!drawing) return;

      const rect = canvas.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      ctx.lineWidth = 3;
      ctx.lineCap = 'round';
      ctx.strokeStyle = '#ff0000';

      ctx.lineTo(x, y);
      ctx.stroke();
      ctx.beginPath();
      ctx.moveTo(x, y);
    };

    const stopDrawing = () => {
      drawing = false;
      ctx.beginPath();
    };

    canvas.addEventListener('mousedown', startDrawing);
    canvas.addEventListener('mousemove', draw);
    canvas.addEventListener('mouseup', stopDrawing);
    canvas.addEventListener('mouseout', stopDrawing);
  };

  // Clear canvas
  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  // Analyze video
  const analyzeVideo = async () => {
    if (!selectedFile) return;

    setIsAnalyzing(true);

    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const base64Data = await fileToBase64(selectedFile);
      const videoData = base64Data.split(',')[1];

      const result = await model.generateContent([
        "Analyze this video and provide a comprehensive summary of the content, key points, and any educational material shown.",
        {
          inlineData: {
            mimeType: selectedFile.type,
            data: videoData
          }
        }
      ]);

      setAnalysisResult(result.response.text());

    } catch (error) {
      console.error('Error:', error);
      setAnalysisResult('Error analyzing video.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üìπ Video Analyzer & Drawing Tool</h1>
        <p>Upload a video, analyze it, and draw notes</p>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <input type="file" accept="video/*" onChange={handleFileSelect} />
          {selectedFile && <p>Selected: {selectedFile.name}</p>}

          <button onClick={analyzeVideo} disabled={!selectedFile || isAnalyzing}>
            {isAnalyzing ? 'Analyzing...' : 'Analyze Video'}
          </button>
        </div>

        <video ref={videoRef} style={{ display: 'none' }} />

        {/* Drawing Canvas */}
        <div className="canvas-section">
          <div style={{ marginBottom: '10px' }}>
            <button 
              onClick={enableDrawing} 
              disabled={isDrawing}
              style={{ 
                padding: '10px 20px', 
                marginRight: '10px',
                backgroundColor: isDrawing ? '#28a745' : '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              {isDrawing ? 'ÔøΩÔ∏è Drawing Enabled' : 'üñäÔ∏è Enable Drawing'}
            </button>
            <button 
              onClick={clearCanvas}
              style={{ 
                padding: '10px 20px',
                backgroundColor: '#dc3545',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              üóëÔ∏è Clear Canvas
            </button>
          </div>

          <canvas 
            ref={canvasRef}
            width="800" 
            height="600"
            style={{ 
              border: '2px solid #333',
              backgroundColor: '#f8f9fa',
              cursor: isDrawing ? 'crosshair' : 'default'
            }}
          />
        </div>

        {/* Analysis Results */}
        {analysisResult && (
          <div className="analysis-section">
            <h3>üìã Video Analysis:</h3>
            <div style={{ 
              padding: '15px',
              backgroundColor: '#f8f9fa',
              border: '1px solid #dee2e6',
              borderRadius: '5px',
              whiteSpace: 'pre-wrap',
              textAlign: 'left'
            }}>
              {analysisResult}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
