import React, { useState, useRef, useCallback } from 'react';
import './App.css';
import { GoogleGenerativeAI } from '@google/generative-ai';
import { Hands } from '@mediapipe/hands';
import { Camera } from '@mediapipe/camera_utils';

// Initialize Gemini AI
const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState('');
  const [lastCompleteFrame, setLastCompleteFrame] = useState(null);

  // Drawing mode state
  const [isDrawingMode, setIsDrawingMode] = useState(false);
  const [isPinching, setIsPinching] = useState(false);
  const [currentPath, setCurrentPath] = useState([]);
  const [allPaths, setAllPaths] = useState([]);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const frameDisplayRef = useRef(null);
  const drawingCanvasRef = useRef(null);
  const handsRef = useRef(null);
  const cameraRef = useRef(null);

  // Initialize hand tracking
  const initializeHandTracking = useCallback(() => {
    if (!handsRef.current) {
      handsRef.current = new Hands({
        locateFile: (file) => {
          return `https://cdn.jsdelivr.net/npm/@mediapipe/hands/${file}`;
        }
      });

      handsRef.current.setOptions({
        maxNumHands: 1,
        modelComplexity: 1,
        minDetectionConfidence: 0.7,
        minTrackingConfidence: 0.5
      });

      handsRef.current.onResults(onHandsResults);
    }
  }, []);

  // Hand tracking results handler
  const onHandsResults = useCallback((results) => {
    if (!isDrawingMode || !drawingCanvasRef.current) return;

    if (results.multiHandLandmarks && results.multiHandLandmarks.length > 0) {
      const landmarks = results.multiHandLandmarks[0];
      const pinchDetected = detectPinchGesture(landmarks);

      if (pinchDetected && !isPinching) {
        setIsPinching(true);
        const point = getPinchPoint(landmarks);
        setCurrentPath([point]);
      } else if (pinchDetected && isPinching) {
        const point = getPinchPoint(landmarks);
        setCurrentPath(prev => [...prev, point]);
        drawSolidLine(point);
      } else if (!pinchDetected && isPinching) {
        setIsPinching(false);
        if (currentPath.length > 0) {
          setAllPaths(prev => [...prev, currentPath]);
          setCurrentPath([]);
        }
      }
    } else {
      if (isPinching) {
        setIsPinching(false);
        if (currentPath.length > 0) {
          setAllPaths(prev => [...prev, currentPath]);
          setCurrentPath([]);
        }
      }
    }
  }, [isDrawingMode, isPinching, currentPath]);

  // Detect pinch gesture
  const detectPinchGesture = (landmarks) => {
    const thumbTip = landmarks[4];
    const indexTip = landmarks[8];

    const distance = Math.sqrt(
      Math.pow(thumbTip.x - indexTip.x, 2) + 
      Math.pow(thumbTip.y - indexTip.y, 2)
    );

    return distance < 0.05;
  };

  // Get pinch point
  const getPinchPoint = (landmarks) => {
    const thumbTip = landmarks[4];
    const indexTip = landmarks[8];
    const canvas = drawingCanvasRef.current;

    return {
      x: ((thumbTip.x + indexTip.x) / 2) * canvas.width,
      y: ((thumbTip.y + indexTip.y) / 2) * canvas.height
    };
  };

  // Draw solid line
  const drawSolidLine = (currentPoint) => {
    const canvas = drawingCanvasRef.current;
    const ctx = canvas.getContext('2d');

    if (currentPath.length > 1) {
      const prevPoint = currentPath[currentPath.length - 2];

      ctx.strokeStyle = '#ff4444';
      ctx.lineWidth = 4;
      ctx.lineCap = 'round';
      ctx.lineJoin = 'round';

      ctx.beginPath();
      ctx.moveTo(prevPoint.x, prevPoint.y);
      ctx.lineTo(currentPoint.x, currentPoint.y);
      ctx.stroke();
    }
  };

  // Start drawing mode
  const startDrawingMode = async () => {
    if (!lastCompleteFrame) {
      alert('Please analyze a video first!');
      return;
    }

    try {
      setIsDrawingMode(true);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 640, height: 480 } 
      });

      initializeHandTracking();

      const hiddenVideo = document.createElement('video');
      hiddenVideo.srcObject = stream;
      hiddenVideo.autoplay = true;
      hiddenVideo.playsInline = true;
      hiddenVideo.muted = true;

      if (handsRef.current) {
        cameraRef.current = new Camera(hiddenVideo, {
          onFrame: async () => {
            if (handsRef.current && hiddenVideo) {
              await handsRef.current.send({ image: hiddenVideo });
            }
          },
          width: 640,
          height: 480
        });
        cameraRef.current.start();
      }

      setupDrawingCanvas();

    } catch (error) {
      console.error('Error starting drawing mode:', error);
      setIsDrawingMode(false);
    }
  };

  // Process voice commands
  const processVoiceCommand = async (command) => {
    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      let prompt = '';
      if (command.includes('explain') || command.includes('what') || command.includes('how')) {
        prompt = `As EDITH, an AI teaching assistant, explain this educational content. The student has drawn ${allPaths.length} circles/markings on the final slide to highlight areas of interest. Focus on those highlighted sections. Educational content: ${analysisResult}. Student question: "${command}". Provide a clear, focused explanation.`;
      } else if (command.includes('summary') || command.includes('summarize')) {
        prompt = `Provide a concise summary of the key points from this educational content, paying attention to areas the student has highlighted by drawing: ${analysisResult}`;
      } else {
        prompt = `As EDITH, respond to this student question about the educational content: "${command}". Context: ${analysisResult}. Note: The student has highlighted ${allPaths.length} areas on the slide for emphasis.`;
      }

      const result = await model.generateContent(prompt);
      const response = await result.response;
      setEdithResponse(response.text());
    } catch (error) {
      console.error('Error processing voice command:', error);
      setEdithResponse('Sorry, I had trouble processing that command.');
    }
  };

  // Stop drawing mode
  const stopDrawingMode = () => {
    setIsDrawingMode(false);
    setIsPinching(false);

    if (cameraRef.current) {
      cameraRef.current.stop();
      cameraRef.current = null;
    }

    clearDrawing();
  };

  // Setup drawing canvas
  const setupDrawingCanvas = () => {
    const canvas = drawingCanvasRef.current;
    const frameDisplay = frameDisplayRef.current;

    if (canvas && frameDisplay) {
      canvas.width = frameDisplay.naturalWidth;
      canvas.height = frameDisplay.naturalHeight;
      canvas.style.position = 'absolute';
      canvas.style.top = '0';
      canvas.style.left = '0';
      canvas.style.pointerEvents = 'none';
    }
  };

  // Clear drawing
  const clearDrawing = () => {
    const canvas = drawingCanvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
    setAllPaths([]);
    setCurrentPath([]);
  };

  // File handling
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      setAnalysisResult('');
      setLastCompleteFrame(null);
    }
  };

  // Convert file to base64
  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = error => reject(error);
    });
  };

  // Capture frame from video
  const captureFrame = (video, timestamp) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    ctx.drawImage(video, 0, 0);

    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        const url = URL.createObjectURL(blob);
        resolve({ url, timestamp });
      });
    });
  };

  // Analyze video and get final frame
  const analyzeVideo = async () => {
    if (!selectedFile) return;

    setIsAnalyzing(true);

    try {
      const video = videoRef.current;
      video.src = URL.createObjectURL(selectedFile);

      await new Promise((resolve) => {
        video.onloadedmetadata = resolve;
      });

      const duration = video.duration;

      // Get the last frame
      video.currentTime = duration - 1;
      await new Promise((resolve) => {
        video.onseeked = resolve;
      });

      const lastFrame = await captureFrame(video, duration - 1);
      setLastCompleteFrame(lastFrame);

      // Analyze with Gemini
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const base64Data = await fileToBase64(selectedFile);
      const videoData = base64Data.split(',')[1];

      const result = await model.generateContent([
        "Analyze this educational video. Focus on the final slide content and key concepts.",
        {
          inlineData: {
            mimeType: selectedFile.type,
            data: videoData
          }
        }
      ]);

      setAnalysisResult(result.response.text());

    } catch (error) {
      console.error('Error:', error);
      setAnalysisResult('Error analyzing video.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üéØ Interactive Educational Analyzer</h1>
        <p>Analyze videos and interact with the final slide</p>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <input type="file" accept="video/*" onChange={handleFileSelect} />
          {selectedFile && <p>Selected: {selectedFile.name}</p>}
        </div>

        <video ref={videoRef} style={{ display: 'none' }} />
        <canvas ref={canvasRef} style={{ display: 'none' }} />

        <button onClick={analyzeVideo} disabled={!selectedFile || isAnalyzing}>
          {isAnalyzing ? 'Analyzing...' : 'Analyze Video'}
        </button>

        {lastCompleteFrame && (
          <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <h3>üñºÔ∏è Final Slide:</h3>
              <div>
                <button 
                  onClick={isDrawingMode ? stopDrawingMode : startDrawingMode}
                  style={{ 
                    padding: '10px 20px',
                    backgroundColor: isDrawingMode ? '#dc3545' : '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginRight: '10px'
                  }}
                >
                  {isDrawingMode ? 'üõë Stop Drawing' : '‚úã Start Hand Drawing'}
                </button>
                {isDrawingMode && (
                  <button 
                    onClick={clearDrawing}
                    style={{ 
                      padding: '10px 20px',
                      backgroundColor: '#6c757d',
                      color: 'white',
                      border: 'none',
                      borderRadius: '5px',
                      cursor: 'pointer'
                    }}
                  >
                    üóëÔ∏è Clear Drawing
                  </button>
                )}
              </div>
            </div>

            <div style={{ position: 'relative', display: 'inline-block' }}>
              <img 
                ref={frameDisplayRef}
                src={lastCompleteFrame.url} 
                alt="Final slide" 
                style={{ maxWidth: '100%', display: 'block' }}
                onLoad={setupDrawingCanvas}
              />
              {isDrawingMode && (
                <canvas
                  ref={drawingCanvasRef}
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    pointerEvents: 'none'
                  }}
                />
              )}
            </div>

            {isDrawingMode && (
              <div style={{ 
                marginTop: '10px', 
                padding: '10px', 
                backgroundColor: '#e9ecef', 
                borderRadius: '5px' 
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    color: isPinching ? '#28a745' : '#6c757d' 
                  }}>
                    ü§è Pinch to draw: {isPinching ? 'Active' : 'Inactive'}
                  </div>
                  <div>
                    Paths drawn: {allPaths.length}
                  </div>
                </div>
                <p style={{ margin: '5px 0 0 0', fontSize: '14px', color: '#6c757d' }}>
                  Use your thumb and index finger to pinch and draw red circles around areas of interest
                </p>
              </div>
            )}
          </div>
        )}

        {analysisResult && (
          <div>
            <h3>Analysis:</h3>
            <pre>{analysisResult}</pre>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
