  // Handle hand tracking results
  const onHandsResults = useCallback((results) => {
    if (!liveCanvasRef.current || !drawingCanvasRef.current) {
      console.warn('‚ö†Ô∏è Canvas refs not available in onHandsResults');
      return;
    }

    const canvas = liveCanvasRef.current;
    const ctx = canvas.getContext('2d');
    const drawingCanvas = drawingCanvasRef.current;
    const drawingCtx = drawingCanvas.getContext('2d');

    if (!ctx || !drawingCtx) {
      console.error('‚ùå Failed to get canvas contexts');
      return;
    }

    // Clear the overlay canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (results.multiHandLandmarks && results.multiHandLandmarks.length > 0) {
      for (const landmarks of results.multiHandLandmarks) {

        // Draw green dots on all fingertips for visual feedback
        const fingertips = [4, 8, 12, 16, 20]; // Thumb, Index, Middle, Ring, Pinky tips
        fingertips.forEach(tipIndex => {
          const tip = landmarks[tipIndex];
          const x = tip.x * canvas.width;
          const y = tip.y * canvas.height;

          ctx.fillStyle = '#00FF00'; // Bright green
          ctx.beginPath();
          ctx.arc(x, y, 8, 0, 2 * Math.PI);
          ctx.fill();

          // Add white border for better visibility
          ctx.strokeStyle = '#FFFFFF';
          ctx.lineWidth = 2;
          ctx.stroke();
        });

        // Check for pointer finger drawing gesture (index finger extended, others curled)
        const thumb = landmarks[4];
        const indexFinger = landmarks[8];
        const indexFingerMcp = landmarks[5]; // Index finger base
        const middleFinger = landmarks[12];
        const middleFingerMcp = landmarks[9]; // Middle finger base
        const ringFinger = landmarks[16];
        const ringFingerMcp = landmarks[13]; // Ring finger base
        const pinky = landmarks[20];
        const pinkyMcp = landmarks[17]; // Pinky base

        // Simplified gesture detection - just check if index finger is extended and pointing
        const indexExtended = indexFinger.y < indexFingerMcp.y - 0.04;

        // Less strict curl detection for better recognition
        const middleCurled = middleFinger.y > middleFingerMcp.y + 0.01;
        const ringCurled = ringFinger.y > ringFingerMcp.y + 0.01;
        const pinkyCurled = pinky.y > pinkyMcp.y + 0.01;

        // Debug logging
        console.log('üëÜ Finger detection:', {
          indexExtended,
          middleCurled,
          ringCurled,
          pinkyCurled,
          indexY: indexFinger.y,
          baseY: indexFingerMcp.y
        });

        // Check for open palm gesture (all fingers extended)
        const fingerTips = [8, 12, 16, 20]; // Index, middle, ring, pinky tips
        const fingerBases = [6, 10, 14, 18]; // Finger bases
        const fingersExtended = fingerTips.every((tip, index) => 
          landmarks[tip].y < landmarks[fingerBases[index]].y - 0.02
        );
        const thumbExtended = landmarks[4].x > landmarks[3].x; // Thumb extended

        // More lenient drawing detection
        if (indexExtended && (middleCurled || ringCurled || pinkyCurled)) {
          // Pointer finger gesture - drawing mode
          const x = indexFinger.x * canvas.width;
          const y = indexFinger.y * canvas.height;

          console.log('‚úèÔ∏è Drawing detected at:', x, y);

          if (!isDrawing) {
            // Starting to draw
            setIsDrawing(true);
            setIsErasing(false);
            setLastDrawPoint({ x, y });

            console.log('üé® Started drawing');

            // Test: Draw a starting point with a larger visible circle
            drawingCtx.globalCompositeOperation = 'source-over';
            drawingCtx.fillStyle = '#4A90E2';
            drawingCtx.beginPath();
            drawingCtx.arc(x, y, 8, 0, 2 * Math.PI);
            drawingCtx.fill();

            // Also draw a stroke around it to make it more visible
            drawingCtx.strokeStyle = '#FFFFFF';
            drawingCtx.lineWidth = 2;
            drawingCtx.stroke();
          } else {
            // Continue drawing - draw line from last point to current point
            if (lastDrawPoint) {
              const distance = Math.sqrt(
                Math.pow(x - lastDrawPoint.x, 2) + Math.pow(y - lastDrawPoint.y, 2)
              );

              // Only draw if finger moved enough (reduces noise)
              if (distance > 3) {
                drawingCtx.globalCompositeOperation = 'source-over';
                drawingCtx.strokeStyle = '#4A90E2';
                drawingCtx.lineWidth = 8;
                drawingCtx.lineCap = 'round';
                drawingCtx.lineJoin = 'round';

                // Draw the main line
                drawingCtx.beginPath();
                drawingCtx.moveTo(lastDrawPoint.x, lastDrawPoint.y);
                drawingCtx.lineTo(x, y);
                drawingCtx.stroke();

                // Add a white outline for better visibility
                drawingCtx.strokeStyle = '#FFFFFF';
                drawingCtx.lineWidth = 10;
                drawingCtx.globalCompositeOperation = 'destination-over';
                drawingCtx.beginPath();
                drawingCtx.moveTo(lastDrawPoint.x, lastDrawPoint.y);
                drawingCtx.lineTo(x, y);
                drawingCtx.stroke();

                // Reset composite operation
                drawingCtx.globalCompositeOperation = 'source-over';

                setLastDrawPoint({ x, y });
                console.log('üìù Drew line to:', x, y);
              }
            }
