    if (timeSinceLastCall < minDelay) {
      const waitTime = Math.ceil((minDelay - timeSinceLastCall) / 1000);
      const waitMessage = `‚è±Ô∏è Please wait ${waitTime} seconds before making another request.`;
      setAiAnalysisResult(waitMessage);
