          drawingCtx.lineCap = 'round';
          drawingCtx.lineJoin = 'round';

          // Draw continuous line
          if (drawingPath.length > 0) {
            const lastPoint = drawingPath[drawingPath.length - 1];
            drawingCtx.beginPath();
            drawingCtx.moveTo(lastPoint.x, lastPoint.y);
            drawingCtx.lineTo(x, y);
            drawingCtx.stroke();
          }

          // Add to drawing path
          setDrawingPath(prev => [...prev, { x, y, type: 'draw', timestamp: Date.now() }]);

          // Visual feedback - highlight the pinch
          ctx.fillStyle = '#4A90E2';
          ctx.beginPath();
          ctx.arc(indexFinger.x * canvas.width, indexFinger.y * canvas.height, 12, 0, 2 * Math.PI);
          ctx.fill();
          ctx.strokeStyle = '#FFFFFF';
          ctx.lineWidth = 3;
          ctx.stroke();

        } else if (fingersExtended && thumbExtended) {
          // Open palm gesture - erase
          setIsDrawing(false);
          setIsErasing(true);

          // Erase in area around palm center
          const palmCenter = landmarks[9]; // Palm center
