import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import { GoogleGenerativeAI } from '@google/generative-ai';
import { Hands } from '@mediapipe/hands';
import { Camera } from '@mediapipe/camera_utils';

// Initialize Gemini AI
const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

function App() {
  const [isLiveMode, setIsLiveMode] = useState(false);
  const [isDrawing, setIsDrawing] = useState(false);
  const [drawingPoints, setDrawingPoints] = useState([]);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const drawingCanvasRef = useRef(null);
  const handsRef = useRef(null);
  const cameraRef = useRef(null);

  // Initialize MediaPipe Hands
  useEffect(() => {
    if (handsRef.current) return;

    handsRef.current = new Hands({
      locateFile: (file) => {
        return `https://cdn.jsdelivr.net/npm/@mediapipe/hands/${file}`;
      }
    });

    handsRef.current.setOptions({
      maxNumHands: 1,
      modelComplexity: 1,
      minDetectionConfidence: 0.7,
      minTrackingConfidence: 0.5
    });

    handsRef.current.onResults(onResults);
  }, []);

  // Initialize MediaPipe Hands
  useEffect(() => {
    if (handsRef.current) return;

    handsRef.current = new Hands({
      locateFile: (file) => {
        return `https://cdn.jsdelivr.net/npm/@mediapipe/hands/${file}`;
      }
    });

    handsRef.current.setOptions({
      maxNumHands: 1,
      modelComplexity: 1,
      minDetectionConfidence: 0.7,
      minTrackingConfidence: 0.5
    });

    handsRef.current.onResults(onResults);
  }, []);

  // Process hand detection results
  const onResults = (results) => {
    const canvas = canvasRef.current;
    const drawingCanvas = drawingCanvasRef.current;

    if (!canvas || !drawingCanvas) return;

    const ctx = canvas.getContext('2d');
    const drawCtx = drawingCanvas.getContext('2d');

    // Clear and draw video frame
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    if (results.multiHandLandmarks && results.multiHandLandmarks.length > 0) {
      const landmarks = results.multiHandLandmarks[0];

      // Draw hand landmarks (green dots)
      landmarks.forEach((landmark, index) => {
        const x = landmark.x * canvas.width;
        const y = landmark.y * canvas.height;

        ctx.beginPath();
        ctx.arc(x, y, 5, 0, 2 * Math.PI);
        ctx.fillStyle = '#00ff00';
        ctx.fill();
      });

      // Check for pointing gesture and draw
      const isPointing = detectPointingGesture(landmarks);

      if (isPointing) {
        const indexTip = landmarks[8];
        const x = indexTip.x * drawingCanvas.width;
        const y = indexTip.y * drawingCanvas.height;

        // Highlight pointing finger
        ctx.beginPath();
        ctx.arc(indexTip.x * canvas.width, indexTip.y * canvas.height, 15, 0, 2 * Math.PI);
        ctx.strokeStyle = '#0066ff';
        ctx.lineWidth = 3;
        ctx.stroke();

        if (!isDrawing) {
          setIsDrawing(true);
          setDrawingPoints([{x, y}]);
          console.log('‚úèÔ∏è Started drawing at:', x, y);
        } else {
          // Continue drawing
          setDrawingPoints(prev => {
            const newPoints = [...prev, {x, y}];
            drawLine(drawCtx, newPoints);
            return newPoints;
          });
        }
      } else {
        if (isDrawing) {
          setIsDrawing(false);
          console.log('üõë Stopped drawing');
        }
      }
    } else {
      if (isDrawing) {
        setIsDrawing(false);
        console.log('üõë No hand detected - stopped drawing');
      }
    }
  };

  // Enhanced gesture detection
  const detectPointingGesture = (landmarks) => {
    // Index finger tip and joints
    const indexTip = landmarks[8];
    const indexPip = landmarks[6];
    const indexMcp = landmarks[5];

    // Middle finger tip and pip
    const middleTip = landmarks[12];
    const middlePip = landmarks[10];

    // Ring finger tip and pip
    const ringTip = landmarks[16];
    const ringPip = landmarks[14];

    // Pinky tip and pip
    const pinkyTip = landmarks[20];
    const pinkyPip = landmarks[18];

    // Check if index finger is extended (tip higher than pip)
    const indexExtended = indexTip.y < indexPip.y && indexPip.y < indexMcp.y;

    // Check if other fingers are curled (tips lower than pips)
    const middleCurled = middleTip.y > middlePip.y;
    const ringCurled = ringTip.y > ringPip.y;
    const pinkyCurled = pinkyTip.y > pinkyPip.y;

    // At least one other finger should be curled
    const otherFingersCurled = middleCurled || ringCurled || pinkyCurled;

    const isPointing = indexExtended && otherFingersCurled;

    if (isPointing) {
      console.log('üëÜ Pointing detected!');
    }

    return isPointing;
  };

  // Draw line on canvas
  const drawLine = (ctx, points) => {
    if (points.length < 2) return;

    const currentPoint = points[points.length - 1];
    const prevPoint = points[points.length - 2];

    // Draw thick blue line with white outline
    ctx.strokeStyle = '#ffffff';
    ctx.lineWidth = 10;
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';

    ctx.beginPath();
    ctx.moveTo(prevPoint.x, prevPoint.y);
    ctx.lineTo(currentPoint.x, currentPoint.y);
    ctx.stroke();

    // Blue inner line
    ctx.strokeStyle = '#0066ff';
    ctx.lineWidth = 8;

    ctx.beginPath();
    ctx.moveTo(prevPoint.x, prevPoint.y);
    ctx.lineTo(currentPoint.x, currentPoint.y);
    ctx.stroke();
  };

  // Start live whiteboard mode
  const startLiveMode = async () => {
    try {
      setIsLiveMode(true);

      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 640, height: 480 }
      });

      const video = videoRef.current;
      video.srcObject = stream;

      await new Promise((resolve) => {
        video.onloadedmetadata = resolve;
      });

      // Setup canvases
      const canvas = canvasRef.current;
      const drawingCanvas = drawingCanvasRef.current;

      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      drawingCanvas.width = video.videoWidth;
      drawingCanvas.height = video.videoHeight;

      // Start camera processing
      if (handsRef.current) {
        cameraRef.current = new Camera(video, {
          onFrame: async () => {
            await handsRef.current.send({ image: video });
          },
          width: 640,
          height: 480
        });
        cameraRef.current.start();
      }

      console.log('üé• Live whiteboard mode started');

    } catch (error) {
      console.error('Error starting live mode:', error);
      setIsLiveMode(false);
    }
  };

  // Stop live mode
  const stopLiveMode = () => {
    setIsLiveMode(false);
    setIsDrawing(false);

    if (cameraRef.current) {
      cameraRef.current.stop();
      cameraRef.current = null;
    }

    if (videoRef.current && videoRef.current.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }

    console.log('üõë Live whiteboard mode stopped');
  };

  // Clear drawing canvas
  const clearDrawing = () => {
    const drawingCanvas = drawingCanvasRef.current;
    if (drawingCanvas) {
      const ctx = drawingCanvas.getContext('2d');
      ctx.clearRect(0, 0, drawingCanvas.width, drawingCanvas.height);
      setDrawingPoints([]);
      console.log('üóëÔ∏è Drawing cleared');
    }
  };

  // File handling (legacy - for video analysis)
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      setAnalysisResult('');
    }
  };

  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = error => reject(error);
    });
  };

  const analyzeVideo = async () => {
    if (!selectedFile) return;
    setIsAnalyzing(true);
    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const base64Data = await fileToBase64(selectedFile);
      const videoData = base64Data.split(',')[1];
      const result = await model.generateContent([
        "Analyze this video and provide a comprehensive summary.",
        { inlineData: { mimeType: selectedFile.type, data: videoData } }
      ]);
      setAnalysisResult(result.response.text());
    } catch (error) {
      console.error('Error:', error);
      setAnalysisResult('Error analyzing video.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>üìπ Video Analyzer & Drawing Tool</h1>
        <p>Upload a video, analyze it, and draw notes</p>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <input type="file" accept="video/*" onChange={handleFileSelect} />
          {selectedFile && <p>Selected: {selectedFile.name}</p>}

          <button onClick={analyzeVideo} disabled={!selectedFile || isAnalyzing}>
            {isAnalyzing ? 'Analyzing...' : 'Analyze Video'}
          </button>
        </div>

        <video ref={videoRef} style={{ display: 'none' }} />

        {/* Analysis Results */}
        {analysisResult && (
          <div className="analysis-section" style={{ marginBottom: '20px' }}>
            <h3>üìã Video Analysis Summary:</h3>
            <div style={{ 
              padding: '15px',
              backgroundColor: '#f8f9fa',
              border: '1px solid #dee2e6',
              borderRadius: '5px',
              whiteSpace: 'pre-wrap',
              textAlign: 'left',
              maxHeight: '300px',
              overflowY: 'auto'
            }}>
              {analysisResult}
            </div>
          </div>
        )}

        {/* Ask EDITH Section */}
        {analysisResult && (
          <div className="edith-section" style={{ marginBottom: '20px' }}>
            <h3>ü§ñ Ask EDITH a Question:</h3>
            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
              <input
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="Ask EDITH about the video content..."
                style={{
                  flex: 1,
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                  fontSize: '16px'
                }}
                onKeyPress={(e) => e.key === 'Enter' && askEdith()}
              />
              <button
                onClick={askEdith}
                disabled={isAsking || !question.trim()}
                style={{
                  padding: '10px 20px',
                  backgroundColor: isAsking ? '#6c757d' : '#28a745',
                  color: 'white',
                  border: 'none',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                {isAsking ? 'Asking...' : 'Ask EDITH'}
              </button>
            </div>

            {edithResponse && (
              <div style={{
                marginTop: '15px',
                padding: '15px',
                backgroundColor: '#e8f5e8',
                border: '1px solid #28a745',
                borderRadius: '5px',
                whiteSpace: 'pre-wrap'
              }}>
                <strong>EDITH:</strong> {edithResponse}
              </div>
            )}
          </div>
        )}

        {/* Drawing Canvas */}
        <div className="canvas-section">
          <div style={{ marginBottom: '10px' }}>
            <button 
              onClick={enableDrawing} 
              disabled={isDrawing}
              style={{ 
                padding: '10px 20px', 
                marginRight: '10px',
                backgroundColor: isDrawing ? '#28a745' : '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              {isDrawing ? 'ÔøΩÔ∏è Drawing Enabled' : 'üñäÔ∏è Enable Drawing'}
            </button>
            <button 
              onClick={clearCanvas}
              style={{ 
                padding: '10px 20px',
                backgroundColor: '#dc3545',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              üóëÔ∏è Clear Canvas
            </button>
          </div>

          <canvas 
            ref={canvasRef}
            width="800" 
            height="600"
            style={{ 
              border: '2px solid #333',
              backgroundColor: '#f8f9fa',
              cursor: isDrawing ? 'crosshair' : 'default'
            }}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
