import React, { useState, useRef } from 'react';
import { GoogleGenerativeAI } from '@google/generative-ai';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState('');
  const [question, setQuestion] = useState('');
  const [isAsking, setIsAsking] = useState(false);
  const [edithResponse, setEdithResponse] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);
  const [boardScreenshot, setBoardScreenshot] = useState(null);

  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const fileInputRef = useRef(null);

  // Initialize Gemini AI
  const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY || 'AIzaSyDK_HUfnVZE8Fw4YiSk3U5TQz-9Lr10WNk');

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      if (videoRef.current) {
        videoRef.current.src = url;
      }
    } else {
      alert('Please select a video file');
    }
  };

  const analyzeVideo = async () => {
    if (!selectedFile) {
      alert('Please select a video file first');
      return;
    }
    setIsAnalyzing(true);

    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      // Convert video to base64 for analysis
      const reader = new FileReader();
      reader.onload = async (e) => {
        const base64Data = e.target.result.split(',')[1];

        const prompt = `Analyze this educational video and provide a comprehensive summary focusing on:

1. üìö MAIN TOPICS: What subjects or concepts are being taught?
2. üìù WHITEBOARD CONTENT: Describe any equations, diagrams, text, or drawings visible on whiteboards/screens
3. üéØ KEY LEARNING POINTS: What are the main educational objectives?
4. üîç VISUAL ELEMENTS: Describe charts, graphs, mathematical formulas, or visual aids shown
5. üìñ EDUCATIONAL VALUE: How would this help students learn?

Please provide a detailed, well-structured analysis that would help someone understand what was covered in this educational content.`;

        try {
          const result = await model.generateContent([
            prompt,
            {
              inlineData: {
                data: base64Data,
                mimeType: selectedFile.type
              }
            }
          ]);

          const response = await result.response;
          setAnalysisResult(response.text());
        } catch (error) {
          console.error('Analysis error:', error);
          setAnalysisResult(`‚ùå Analysis Error: ${error.message}\n\nTip: Make sure you have a valid Google Gemini API key set in your environment variables.`);
        }
      };

      reader.readAsDataURL(selectedFile);
    } catch (error) {
      console.error('Error:', error);
      setAnalysisResult(`‚ùå Error: ${error.message}`);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const askEdith = async () => {
    if (!question.trim()) {
      alert('Please enter a question');
      return;
    }
    setIsAsking(true);

    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      const context = analysisResult ? 
        `Based on this educational content analysis: ${analysisResult}\n\n` : 
        'Based on the educational video content, ';

      const prompt = `${context}Please answer this question in a helpful, educational manner: "${question}"

Provide a clear, detailed response that:
- Directly answers the question
- Relates to the educational content if applicable
- Includes examples or explanations when helpful
- Uses a friendly, teaching tone`;

      const result = await model.generateContent(prompt);
      const response = await result.response;
      setEdithResponse(response.text());
    } catch (error) {
      console.error('Error asking EDITH:', error);
      setEdithResponse(`‚ùå Error: ${error.message}\n\nPlease check your internet connection and API key.`);
    } finally {
      setIsAsking(false);
    }
  };

  // Capture current video frame for board analysis
  const captureBoard = () => {
    if (!videoRef.current) {
      alert('Please load a video first');
      return;
    }

    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    canvas.width = video.videoWidth || 640;
    canvas.height = video.videoHeight || 480;

    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    const screenshot = canvas.toDataURL('image/png');
    setBoardScreenshot(screenshot);

    // Automatically analyze the captured board
    analyzeBoardContent(screenshot);
  };

  // Analyze board content using AI
  const analyzeBoardContent = async (imageData) => {
    if (!imageData) return;

    setIsAnalyzing(true);
    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      const base64Data = imageData.split(',')[1];
      const prompt = `Analyze this whiteboard/screen capture and provide a detailed summary of:

üìù TEXT CONTENT: Any text, equations, formulas, or written content
üìä VISUAL ELEMENTS: Diagrams, charts, graphs, drawings, or illustrations  
üî¢ MATHEMATICAL CONTENT: Equations, calculations, mathematical expressions
üìö EDUCATIONAL TOPICS: What subjects or concepts are being taught
üéØ KEY POINTS: Main ideas or learning objectives visible

Please provide a comprehensive analysis of what's shown on this board/screen.`;

      const result = await model.generateContent([
        prompt,
        {
          inlineData: {
            data: base64Data,
            mimeType: 'image/png'
          }
        }
      ]);

      const response = await result.response;
      setAnalysisResult(response.text());
    } catch (error) {
      console.error('Board analysis error:', error);
      setAnalysisResult(`‚ùå Board Analysis Error: ${error.message}`);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const enableDrawing = () => {
    setIsDrawing(!isDrawing);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      <header style={{
        textAlign: 'center',
        padding: '30px 20px',
        background: 'rgba(0, 0, 0, 0.1)',
        backdropFilter: 'blur(10px)'
      }}>
        <h1 style={{ 
          fontSize: '42px', 
          margin: '0 0 15px 0',
          textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)',
          fontWeight: '700'
        }}>
          üéì Smart Lecture Board Analyzer
        </h1>
        <p style={{ 
          fontSize: '20px', 
          margin: '0',
          opacity: '0.9',
          fontWeight: '300'
        }}>
          AI-powered whiteboard content analysis and interactive Q&A
        </p>
      </header>

      <main style={{
        padding: '30px 20px',
        maxWidth: '1400px',
        margin: '0 auto'
      }}>
        {/* Upload Section with Enhanced Design */}
        <div style={{
          textAlign: 'center',
          marginBottom: '50px',
          background: 'rgba(255, 255, 255, 0.1)',
          padding: '40px',
          borderRadius: '25px',
          backdropFilter: 'blur(15px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          boxShadow: '0 15px 35px rgba(0, 0, 0, 0.2)'
        }}>
          <h2 style={{ 
            fontSize: '28px', 
            marginBottom: '25px',
            color: '#fff',
            fontWeight: '600'
          }}>
            üìÅ Upload Your Educational Video
          </h2>
          <input
            type="file"
            accept="video/*"
            onChange={handleFileSelect}
            ref={fileInputRef}
            style={{ display: 'none' }}
          />
          <button
            onClick={() => fileInputRef.current?.click()}
            style={{
              background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
              color: 'white',
              border: 'none',
              padding: '25px 50px',
              fontSize: '24px',
              borderRadius: '50px',
              cursor: 'pointer',
              boxShadow: '0 15px 35px rgba(255, 107, 107, 0.4)',
              transition: 'all 0.3s ease',
              fontWeight: '600',
              textTransform: 'uppercase',
              letterSpacing: '1px'
            }}
            onMouseOver={(e) => {
              e.target.style.transform = 'translateY(-5px)';
              e.target.style.boxShadow = '0 20px 40px rgba(255, 107, 107, 0.6)';
            }}
            onMouseOut={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 15px 35px rgba(255, 107, 107, 0.4)';
            }}
          >
            üé¨ Choose Video File
          </button>
          {selectedFile && (
            <div style={{
              marginTop: '25px',
              fontSize: '18px',
              background: 'rgba(76, 175, 80, 0.2)',
              padding: '15px 25px',
              borderRadius: '25px',
              display: 'inline-block',
              border: '2px solid rgba(76, 175, 80, 0.3)'
            }}>
              ‚úÖ <strong>Ready:</strong> {selectedFile.name}
            </div>
          )}
        </div>

        {selectedFile && (
          <div style={{
            margin: '40px 0',
            background: 'rgba(255, 255, 255, 0.1)',
            padding: '30px',
            borderRadius: '20px',
            textAlign: 'center'
          }}>
            <video
              ref={videoRef}
              controls
              style={{
                maxWidth: '100%',
                height: 'auto',
                borderRadius: '15px',
                marginBottom: '30px',
                boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
              }}
            />
            <div style={{
              display: 'flex',
              gap: '20px',
              justifyContent: 'center',
              flexWrap: 'wrap'
            }}>
              <button
                onClick={analyzeVideo}
                disabled={isAnalyzing}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: 'linear-gradient(45deg, #4ecdc4, #44a08d)',
                  color: 'white',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                {isAnalyzing ? 'üîÑ Analyzing...' : 'ü§ñ Analyze Video'}
              </button>

              <button
                onClick={enableDrawing}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: isDrawing
                    ? 'linear-gradient(45deg, #ff6b6b, #ee5a24)'
                    : 'linear-gradient(45deg, #ffecd2, #fcb69f)',
                  color: isDrawing ? 'white' : '#333',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                {isDrawing ? '‚úèÔ∏è Drawing On' : '‚úèÔ∏è Enable Drawing'}
              </button>

              <button
                onClick={clearCanvas}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: 'linear-gradient(45deg, #ff9a8b, #f6416c)',
                  color: 'white',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                üóëÔ∏è Clear Drawing
              </button>
            </div>
          </div>
        )}

        {analysisResult && (
          <div style={{
            background: 'rgba(255, 255, 255, 0.15)',
            margin: '50px 0',
            padding: '40px',
            borderRadius: '25px',
            border: '2px solid rgba(255, 255, 255, 0.2)',
            boxShadow: '0 20px 40px rgba(0, 0, 0, 0.2)'
          }}>
            <h2 style={{
              fontSize: '38px',
              marginBottom: '30px',
              textAlign: 'center',
              textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)'
            }}>
              üìä Video Analysis Summary
            </h2>
            <div style={{
              background: 'rgba(0, 0, 0, 0.4)',
              padding: '35px',
              borderRadius: '20px',
              minHeight: '200px'
            }}>
              <pre style={{
                fontSize: '18px',
                lineHeight: '1.8',
                color: '#fff',
                textAlign: 'left',
                whiteSpace: 'pre-wrap',
                fontFamily: 'Georgia, serif',
                margin: 0
              }}>
                {analysisResult}
              </pre>
            </div>
          </div>
        )}

        <div style={{
          background: 'rgba(255, 255, 255, 0.12)',
          padding: '40px',
          borderRadius: '25px',
          margin: '40px 0',
          border: '1px solid rgba(255, 255, 255, 0.2)'
        }}>
          <h2 style={{
            fontSize: '32px',
            marginBottom: '30px',
            textAlign: 'center',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)'
          }}>
            ü§î Ask EDITH Questions
          </h2>
          <div style={{
            display: 'flex',
            gap: '20px',
            marginBottom: '30px',
            alignItems: 'center',
            justifyContent: 'center',
            flexWrap: 'wrap'
          }}>
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask a question about the video content..."
              style={{
                flex: 1,
                minWidth: '350px',
                padding: '20px 25px',
                fontSize: '18px',
                border: '3px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '15px',
                background: 'rgba(255, 255, 255, 0.15)',
                color: 'white'
              }}
              onKeyPress={(e) => e.key === 'Enter' && askEdith()}
            />
            <button
              onClick={askEdith}
              disabled={isAsking}
              style={{
                padding: '20px 40px',
                fontSize: '18px',
                border: 'none',
                borderRadius: '15px',
                cursor: 'pointer',
                background: 'linear-gradient(45deg, #a8edea, #fed6e3)',
                color: '#333',
                boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)',
                fontWeight: 'bold'
              }}
            >
              {isAsking ? 'üîÑ Asking...' : 'ü§ñ Ask EDITH'}
            </button>
          </div>

          {edithResponse && (
            <div style={{
              background: 'rgba(0, 0, 0, 0.3)',
              padding: '30px',
              borderRadius: '20px',
              borderLeft: '8px solid #4ecdc4',
              minHeight: '120px'
            }}>
              <h3 style={{
                color: '#4ecdc4',
                marginBottom: '20px',
                fontSize: '24px',
                textShadow: '1px 1px 2px rgba(0, 0, 0, 0.5)'
              }}>
                üí¨ EDITH's Response:
              </h3>
              <pre style={{
                fontSize: '18px',
                lineHeight: '1.8',
                textAlign: 'left',
                color: '#fff',
                fontFamily: 'Georgia, serif',
                whiteSpace: 'pre-wrap',
                margin: 0
              }}>
                {edithResponse}
              </pre>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
