import React, { useState, useRef } from 'react';
import './App.css';
import { GoogleGenerativeAI } from '@google/generative-ai';

// Initialize Gemini AI
const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // File handling
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      setAnalysisResult('');
    }
  };

  // Convert file to base64
  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = error => reject(error);
    });
  };

  // Simple drawing on canvas
  const enableDrawing = () => {
    setIsDrawing(true);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let drawing = false;

    const startDrawing = (e) => {
      drawing = true;
      draw(e);
    };

    const draw = (e) => {
      if (!drawing) return;

      const rect = canvas.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      ctx.lineWidth = 3;
      ctx.lineCap = 'round';
      ctx.strokeStyle = '#ff0000';

      ctx.lineTo(x, y);
      ctx.stroke();
      ctx.beginPath();
      ctx.moveTo(x, y);
    };

    const stopDrawing = () => {
      drawing = false;
      ctx.beginPath();
    };

    canvas.addEventListener('mousedown', startDrawing);
    canvas.addEventListener('mousemove', draw);
    canvas.addEventListener('mouseup', stopDrawing);
    canvas.addEventListener('mouseout', stopDrawing);
  };

  // Clear canvas
  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  // Analyze video
  const analyzeVideo = async () => {
    if (!selectedFile) return;

    setIsAnalyzing(true);

    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const base64Data = await fileToBase64(selectedFile);
      const videoData = base64Data.split(',')[1];

      const result = await model.generateContent([
        "Analyze this video and provide a comprehensive summary of the content, key points, and any educational material shown.",
        {
          inlineData: {
            mimeType: selectedFile.type,
            data: videoData
          }
        }
      ]);

      setAnalysisResult(result.response.text());

    } catch (error) {
      console.error('Error:', error);
      setAnalysisResult('Error analyzing video.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>🎯 Video Analyzer with Drawing</h1>
        <p>Analyze videos and draw on the final slide</p>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <input type="file" accept="video/*" onChange={handleFileSelect} />
          {selectedFile && <p>Selected: {selectedFile.name}</p>}
        </div>

        <video ref={videoRef} style={{ display: 'none' }} />
        <canvas ref={canvasRef} style={{ display: 'none' }} />

        <button onClick={analyzeVideo} disabled={!selectedFile || isAnalyzing}>
          {isAnalyzing ? 'Analyzing...' : 'Analyze Video'}
        </button>

        {lastCompleteFrame && (
          <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <h3>🖼️ Final Slide:</h3>
              <div>
                <button 
                  onClick={isDrawingMode ? stopDrawingMode : startDrawingMode}
                  style={{ 
                    padding: '10px 20px',
                    backgroundColor: isDrawingMode ? '#dc3545' : '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                    marginRight: '10px'
                  }}
                >
                  {isDrawingMode ? '🛑 Stop Drawing' : '✋ Start Hand Drawing'}
                </button>
                {isDrawingMode && (
                  <button 
                    onClick={clearDrawing}
                    style={{ 
                      padding: '10px 20px',
                      backgroundColor: '#6c757d',
                      color: 'white',
                      border: 'none',
                      borderRadius: '5px',
                      cursor: 'pointer'
                    }}
                  >
                    🗑️ Clear Drawing
                  </button>
                )}
              </div>
            </div>

            <div style={{ position: 'relative', display: 'inline-block' }}>
              <img 
                ref={frameDisplayRef}
                src={lastCompleteFrame.url} 
                alt="Final slide" 
                style={{ maxWidth: '100%', display: 'block' }}
                onLoad={setupDrawingCanvas}
              />
              {isDrawingMode && (
                <canvas
                  ref={drawingCanvasRef}
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    pointerEvents: 'none'
                  }}
                />
              )}
            </div>

            {isDrawingMode && (
              <div style={{ 
                marginTop: '10px', 
                padding: '10px', 
                backgroundColor: '#e9ecef', 
                borderRadius: '5px' 
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    color: isPinching ? '#28a745' : '#6c757d' 
                  }}>
                    🤏 Pinch to draw: {isPinching ? 'Active' : 'Inactive'}
                  </div>
                  <div>
                    Paths drawn: {allPaths.length}
                  </div>
                </div>
                <p style={{ margin: '5px 0 0 0', fontSize: '14px', color: '#6c757d' }}>
                  Use your thumb and index finger to pinch and draw red circles around areas of interest
                </p>
              </div>
            )}
          </div>
        )}

        {analysisResult && (
          <div>
            <h3>📋 Analysis Summary:</h3>
            <div style={{ 
              padding: '15px', 
              backgroundColor: '#f8f9fa', 
              borderRadius: '5px',
              border: '1px solid #dee2e6',
              whiteSpace: 'pre-wrap'
            }}>
              {analysisResult}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
