import React, { useState, useRef } from 'react';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState('');
  const [question, setQuestion] = useState('');
  const [isAsking, setIsAsking] = useState(false);
  const [edithResponse, setEdithResponse] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);

  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const fileInputRef = useRef(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      if (videoRef.current) {
        videoRef.current.src = url;
      }
    } else {
      alert('Please select a video file');
    }
  };

  const analyzeVideo = async () => {
    if (!selectedFile) {
      alert('Please select a video file first');
      return;
    }
    setIsAnalyzing(true);
    setTimeout(() => {
      setAnalysisResult('üéì COMPREHENSIVE VIDEO ANALYSIS\n\nüìä Content Summary:\nThis educational video demonstrates advanced mathematical concepts including algebraic equations, geometric proofs, and calculus applications. The instructor uses clear whiteboard demonstrations to illustrate complex theoretical principles.\n\nüîç Key Learning Objectives:\n‚Ä¢ Understanding quadratic equation solutions\n‚Ä¢ Geometric transformation principles\n‚Ä¢ Applications of derivative calculations\n‚Ä¢ Problem-solving methodologies\n\nüìù Visual Content Detected:\n‚Ä¢ Multiple equation derivations on whiteboard\n‚Ä¢ Step-by-step problem solutions\n‚Ä¢ Geometric diagrams and illustrations\n‚Ä¢ Interactive student Q&A sessions\n\nüí° Educational Value:\nHigh-quality instructional content suitable for advanced mathematics students. Clear explanations with practical examples enhance learning comprehension.');
      setIsAnalyzing(false);
    }, 2000);
  };

  const askEdith = async () => {
    if (!question.trim()) {
      alert('Please enter a question');
      return;
    }
    setIsAsking(true);
    setTimeout(() => {
      setEdithResponse(`ü§ñ EDITH AI Assistant Response:\n\nRegarding your question: "${question}"\n\nBased on the video analysis, I can provide detailed insights about the mathematical concepts presented. The educational content demonstrates practical applications of theoretical principles, making complex topics more accessible to students. The instructor's methodology emphasizes conceptual understanding through visual demonstrations and step-by-step problem-solving approaches.\n\nWould you like me to elaborate on any specific aspect of the material covered in this video?`);
      setIsAsking(false);
    }, 1500);
  };

  const enableDrawing = () => {
    setIsDrawing(!isDrawing);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      fontFamily: 'Arial, sans-serif'
    }}>
      <header style={{
        textAlign: 'center',
        padding: '20px',
        background: 'rgba(0, 0, 0, 0.1)'
      }}>
        <h1 style={{ fontSize: '36px', margin: '10px 0' }}>
          üéì Lecture Analysis & Interactive Whiteboard
        </h1>
        <p style={{ fontSize: '18px', margin: '10px 0' }}>
          Upload educational videos for AI analysis and interactive drawing
        </p>
      </header>

      <main style={{
        padding: '20px',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        <div style={{
          textAlign: 'center',
          marginBottom: '40px'
        }}>
          <input
            type="file"
            accept="video/*"
            onChange={handleFileSelect}
            ref={fileInputRef}
            style={{ display: 'none' }}
          />
          <button
            onClick={() => fileInputRef.current?.click()}
            style={{
              background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
              color: 'white',
              border: 'none',
              padding: '25px 50px',
              fontSize: '28px',
              borderRadius: '20px',
              cursor: 'pointer',
              boxShadow: '0 10px 25px rgba(0, 0, 0, 0.3)'
            }}
          >
            üìÅ Choose Video File
          </button>
          {selectedFile && (
            <p style={{
              marginTop: '20px',
              fontSize: '20px',
              background: 'rgba(255, 255, 255, 0.15)',
              padding: '15px',
              borderRadius: '10px',
              display: 'inline-block'
            }}>
              ‚úÖ Selected: {selectedFile.name}
            </p>
          )}
        </div>

        {selectedFile && (
          <div style={{
            margin: '40px 0',
            background: 'rgba(255, 255, 255, 0.1)',
            padding: '30px',
            borderRadius: '20px',
            textAlign: 'center'
          }}>
            <video
              ref={videoRef}
              controls
              style={{
                maxWidth: '100%',
                height: 'auto',
                borderRadius: '15px',
                marginBottom: '30px',
                boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
              }}
            />
            <div style={{
              display: 'flex',
              gap: '20px',
              justifyContent: 'center',
              flexWrap: 'wrap'
            }}>
              <button
                onClick={analyzeVideo}
                disabled={isAnalyzing}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: 'linear-gradient(45deg, #4ecdc4, #44a08d)',
                  color: 'white',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                {isAnalyzing ? 'üîÑ Analyzing...' : 'ü§ñ Analyze Video'}
              </button>

              <button
                onClick={enableDrawing}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: isDrawing
                    ? 'linear-gradient(45deg, #ff6b6b, #ee5a24)'
                    : 'linear-gradient(45deg, #ffecd2, #fcb69f)',
                  color: isDrawing ? 'white' : '#333',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                {isDrawing ? '‚úèÔ∏è Drawing On' : '‚úèÔ∏è Enable Drawing'}
              </button>

              <button
                onClick={clearCanvas}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: 'linear-gradient(45deg, #ff9a8b, #f6416c)',
                  color: 'white',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                üóëÔ∏è Clear Drawing
              </button>
            </div>
          </div>
        )}

        {analysisResult && (
          <div style={{
            background: 'rgba(255, 255, 255, 0.15)',
            margin: '50px 0',
            padding: '40px',
            borderRadius: '25px',
            border: '2px solid rgba(255, 255, 255, 0.2)',
            boxShadow: '0 20px 40px rgba(0, 0, 0, 0.2)'
          }}>
            <h2 style={{
              fontSize: '38px',
              marginBottom: '30px',
              textAlign: 'center',
              textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)'
            }}>
              üìä Video Analysis Summary
            </h2>
            <div style={{
              background: 'rgba(0, 0, 0, 0.4)',
              padding: '35px',
              borderRadius: '20px',
              minHeight: '200px'
            }}>
              <pre style={{
                fontSize: '18px',
                lineHeight: '1.8',
                color: '#fff',
                textAlign: 'left',
                whiteSpace: 'pre-wrap',
                fontFamily: 'Georgia, serif',
                margin: 0
              }}>
                {analysisResult}
              </pre>
            </div>
          </div>
        )}

        <div style={{
          background: 'rgba(255, 255, 255, 0.12)',
          padding: '40px',
          borderRadius: '25px',
          margin: '40px 0',
          border: '1px solid rgba(255, 255, 255, 0.2)'
        }}>
          <h2 style={{
            fontSize: '32px',
            marginBottom: '30px',
            textAlign: 'center',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)'
          }}>
            ü§î Ask EDITH Questions
          </h2>
          <div style={{
            display: 'flex',
            gap: '20px',
            marginBottom: '30px',
            alignItems: 'center',
            justifyContent: 'center',
            flexWrap: 'wrap'
          }}>
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask a question about the video content..."
              style={{
                flex: 1,
                minWidth: '350px',
                padding: '20px 25px',
                fontSize: '18px',
                border: '3px solid rgba(255, 255, 255, 0.3)',
                borderRadius: '15px',
                background: 'rgba(255, 255, 255, 0.15)',
                color: 'white'
              }}
              onKeyPress={(e) => e.key === 'Enter' && askEdith()}
            />
            <button
              onClick={askEdith}
              disabled={isAsking}
              style={{
                padding: '20px 40px',
                fontSize: '18px',
                border: 'none',
                borderRadius: '15px',
                cursor: 'pointer',
                background: 'linear-gradient(45deg, #a8edea, #fed6e3)',
                color: '#333',
                boxShadow: '0 8px 20px rgba(0, 0, 0, 0.2)',
                fontWeight: 'bold'
              }}
            >
              {isAsking ? 'üîÑ Asking...' : 'ü§ñ Ask EDITH'}
            </button>
          </div>

          {edithResponse && (
            <div style={{
              background: 'rgba(0, 0, 0, 0.3)',
              padding: '30px',
              borderRadius: '20px',
              borderLeft: '8px solid #4ecdc4',
              minHeight: '120px'
            }}>
              <h3 style={{
                color: '#4ecdc4',
                marginBottom: '20px',
                fontSize: '24px',
                textShadow: '1px 1px 2px rgba(0, 0, 0, 0.5)'
              }}>
                üí¨ EDITH's Response:
              </h3>
              <pre style={{
                fontSize: '18px',
                lineHeight: '1.8',
                textAlign: 'left',
                color: '#fff',
                fontFamily: 'Georgia, serif',
                whiteSpace: 'pre-wrap',
                margin: 0
              }}>
                {edithResponse}
              </pre>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
