import React, { useState, useRef } from 'react';
import { GoogleGenerativeAI } from '@google/generative-ai';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState('');
  const [question, setQuestion] = useState('');
  const [isAsking, setIsAsking] = useState(false);
  const [edithResponse, setEdithResponse] = useState('');
  const [isDrawing, setIsDrawing] = useState(false);
  const [boardScreenshot, setBoardScreenshot] = useState(null);

  const canvasRef = useRef(null);
  const videoRef = useRef(null);
  const fileInputRef = useRef(null);

  // Initialize Gemini AI
  const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY || 'AIzaSyDK_HUfnVZE8Fw4YiSk3U5TQz-9Lr10WNk');

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      if (videoRef.current) {
        videoRef.current.src = url;
      }
    } else {
      alert('Please select a video file');
    }
  };

  const analyzeVideo = async () => {
    if (!selectedFile) {
      alert('Please select a video file first');
      return;
    }
    setIsAnalyzing(true);

    try {
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

      // Convert video to base64 for analysis
      const reader = new FileReader();
      reader.onload = async (e) => {
        const base64Data = e.target.result.split(',')[1];

        const prompt = `Analyze this educational video and provide a comprehensive summary focusing on:

1. üìö MAIN TOPICS: What subjects or concepts are being taught?
2. üìù WHITEBOARD CONTENT: Describe any equations, diagrams, text, or drawings visible on whiteboards/screens
3. üéØ KEY LEARNING POINTS: What are the main educational objectives?
4. üîç VISUAL ELEMENTS: Describe charts, graphs, mathematical formulas, or visual aids shown
5. üìñ EDUCATIONAL VALUE: How would this help students learn?

Please provide a detailed, well-structured analysis that would help someone understand what was covered in this educational content.`;

        try {
          const result = await model.generateContent([
            prompt,
            {
              inlineData: {
                data: base64Data,
                mimeType: selectedFile.type
              }
            }
          ]);

          const response = await result.response;
          setAnalysisResult(response.text());
        } catch (error) {
          console.error('Analysis error:', error);
          setAnalysisResult(`‚ùå Analysis Error: ${error.message}\n\nTip: Make sure you have a valid Google Gemini API key set in your environment variables.`);
        }
      };

      reader.readAsDataURL(selectedFile);
    } catch (error) {
      console.error('Error:', error);
      setAnalysisResult(`‚ùå Error: ${error.message}`);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const askEdith = async () => {
    if (!question.trim()) {
      alert('Please enter a question');
      return;
    }
    setIsAsking(true);
    setTimeout(() => {
      setEdithResponse(`ü§ñ EDITH AI Assistant Response:\n\nRegarding your question: "${question}"\n\nBased on the video analysis, I can provide detailed insights about the mathematical concepts presented. The educational content demonstrates practical applications of theoretical principles, making complex topics more accessible to students. The instructor's methodology emphasizes conceptual understanding through visual demonstrations and step-by-step problem-solving approaches.\n\nWould you like me to elaborate on any specific aspect of the material covered in this video?`);
      setIsAsking(false);
    }, 1500);
  };

  const enableDrawing = () => {
    setIsDrawing(!isDrawing);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      fontFamily: 'Arial, sans-serif'
    }}>
      <header style={{
        textAlign: 'center',
        padding: '20px',
        background: 'rgba(0, 0, 0, 0.1)'
      }}>
        <h1 style={{ fontSize: '36px', margin: '10px 0' }}>
          üéì Lecture Analysis & Interactive Whiteboard
        </h1>
        <p style={{ fontSize: '18px', margin: '10px 0' }}>
          Upload educational videos for AI analysis and interactive drawing
        </p>
      </header>

      <main style={{
        padding: '20px',
        maxWidth: '1200px',
        margin: '0 auto'
      }}>
        <div style={{
          textAlign: 'center',
          marginBottom: '40px'
        }}>
          <input
            type="file"
            accept="video/*"
            onChange={handleFileSelect}
            ref={fileInputRef}
            style={{ display: 'none' }}
          />
          <button
            onClick={() => fileInputRef.current?.click()}
            style={{
              background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
              color: 'white',
              border: 'none',
              padding: '25px 50px',
              fontSize: '28px',
              borderRadius: '20px',
              cursor: 'pointer',
              boxShadow: '0 10px 25px rgba(0, 0, 0, 0.3)'
            }}
          >
            üìÅ Choose Video File
          </button>
          {selectedFile && (
            <p style={{
              marginTop: '20px',
              fontSize: '20px',
              background: 'rgba(255, 255, 255, 0.15)',
              padding: '15px',
              borderRadius: '10px',
              display: 'inline-block'
            }}>
              ‚úÖ Selected: {selectedFile.name}
            </p>
          )}
        </div>

        {selectedFile && (
          <div style={{
            margin: '40px 0',
            background: 'rgba(255, 255, 255, 0.1)',
            padding: '30px',
            borderRadius: '20px',
            textAlign: 'center'
          }}>
            <video
              ref={videoRef}
              controls
              style={{
                maxWidth: '100%',
                height: 'auto',
                borderRadius: '15px',
                marginBottom: '30px',
                boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)'
              }}
            />
            <div style={{
              display: 'flex',
              gap: '20px',
              justifyContent: 'center',
              flexWrap: 'wrap'
            }}>
              <button
                onClick={analyzeVideo}
                disabled={isAnalyzing}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: 'linear-gradient(45deg, #4ecdc4, #44a08d)',
                  color: 'white',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                {isAnalyzing ? 'üîÑ Analyzing...' : 'ü§ñ Analyze Video'}
              </button>

              <button
                onClick={enableDrawing}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: isDrawing
                    ? 'linear-gradient(45deg, #ff6b6b, #ee5a24)'
                    : 'linear-gradient(45deg, #ffecd2, #fcb69f)',
                  color: isDrawing ? 'white' : '#333',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                {isDrawing ? '‚úèÔ∏è Drawing On' : '‚úèÔ∏è Enable Drawing'}
              </button>

              <button
                onClick={clearCanvas}
                style={{
                  padding: '18px 35px',
                  fontSize: '18px',
                  border: 'none',
                  borderRadius: '12px',
                  cursor: 'pointer',
                  background: 'linear-gradient(45deg, #ff9a8b, #f6416c)',
                  color: 'white',
                  boxShadow: '0 5px 15px rgba(0, 0, 0, 0.2)'
                }}
              >
                üóëÔ∏è Clear Drawing
              </button>
            </div>
